rule.example:

public class Foo {
 public void bar() {
  try {
   // do something
  } catch (Throwable th) {  //Should not catch throwable
   th.printStackTrace();
  }
 }
}


public class Foo {
 void bar() {
  try {
   try {
   } catch (Exception e) {
    throw new WrapperException(e);
    // this is essentially a GOTO to the WrapperException catch block
   }
  } catch (WrapperException e) {
   // do some more stuff
  }
 }
}


public class Foo {
 void bar() {
  try {
   // do something
   }  catch (NullPointerException npe) {
  }
 }
}


public class Foo {
public void bar() throws Exception {
  throw new Exception();
 }
}


public class Foo {
 void bar() {
  throw new NullPointerException();
 }
}


public class Foo {
 void bar() {
  try {
  // do something
  }  catch (SomeException se) {
     throw se;
  }
 }
}
