public class Foo {
    private static final SimpleDateFormat sdf = new SimpleDateFormat();
    synchronized void bar() {
        sdf.format();
    }
}

public class Foo {
    private static final SimpleDateFormat sdf = new SimpleDateFormat();
    public void bar() {
        synchronized (sdf) {
            sdf.format();
        }
    }
}

