rule.example:

public class Foo {
 public void bar() {
  try {
   // do something
  } catch (Throwable th) {  //Should not catch throwable
   th.printStackTrace();
  }
 }
}


public class Foo {
 void bar() {
  try {
   try {
   } catch (Exception e) {
    throw new WrapperException(e);
    // this is essentially a GOTO to the WrapperException catch block
   }
  } catch (WrapperException e) {
   // do some more stuff
  }
 }
}


public class Foo {
 void bar() {
  try {
   // do something
   }  catch (NullPointerException npe) {
  }
 }
}


public class Foo {
public void bar() throws Exception {
  throw new Exception();
 }
}


public class Foo {
 void bar() {
  throw new NullPointerException();
 }
}


public class Foo {
 void bar() {
  try {
  // do something
  }  catch (SomeException se) {
     throw se;
  }
 }
}


public class Foo 
{
	public void bar()
	{
		try {
			// Here do some stuff
		}
		catch( Exception e) {
			// Handling the issue
		}
		finally 
		{
			// is this really a good idea ?
			throw new Exception();
		}
	}
}


public class Foo {
 void bar() {
  try {
   // do something
  }  catch (SomeException se) {
     // harmless comment      
       throw new SomeException(se);
  }
 }
}


package com.igate.primitive;

public class PrimitiveType {

  public void downCastPrimitiveType() {
    try {
      System.out.println(" i [" + i + "]");
    } catch(Exception e) {
      e.printStackTrace();
    } catch(RuntimeException e) {
      e.printStackTrace();
    } catch(NullPointerException e) {
      e.printStackTrace();
    }
  } 
}

public class Foo {
 void bar() {
  try {
   // do something
  } catch (SomeException se) {
    se.getMessage();
  }
 }
}
